const captureImage = async () => {
  if (!captureDiv.value) return;

  const originalMaskState = showPreviewMask.value;
  showPreviewMask.value = false;

  // ç­‰å¾…å­—ä½“åŠ è½½ + DOM å‘æŒ¥å¸ƒå±€
